// 3) La fila 0 de allValues es el array de encabezados (nombres de columnas)
const headers = allValues[0];

// 4) A partir de la fila 1 empiezan los datos reales
const rows = allValues.slice(1);

// 5) Convertimos cada fila en un objeto:
//    - Map devuelve un nuevo array con el mismo número de filas.
//    - Por cada fila, creamos un objeto vacío.
//    - Recorremos cada encabezado (headers) para asignar:
//        obj[ headers[i] ] = row[i] (o '' si no existe valor).
const objects = rows.map(row => {
    const obj = {};                          // Objeto para esta fila
    headers.forEach((colName, idx) => {
        // Cada columna recibe su valor; si falta, ponemos cadena vacía
        obj[colName] = row[idx] !== undefined ? row[idx] : '';
    });
    return obj;                              // Devolvemos el objeto construido
});

// 6) Finalmente, msg.payload debe ser el array de objetos
msg.payload = objects;

